import React, { useEffect } from "react";
import { WOW } from "wowjs";
import "animate.css";

function App() {
  useEffect(() => {
    new WOW({
      live: false
    }).init();
  }, []);

  return (
    <div className="App">
      <h1 className="wow fadeInUp" data-wow-delay="0.3s">
        Hello Animation!
      </h1>
    </div>
  );
}

export default App;
